void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
	vec2 p = fragCoord.xy;
	p.y = p.y + iResolution.y;	// Move the point far away so we don't see the origin
	vec2 q = -1.0 + 2.0 * p / iResolution.xy;

	float a = -2.2;
	float b = -1.6;
	float c = 0.4;
	float d = -2.;

	float angle = degrees(atan(q.x, q.y)) / 4.0;

	float t = angle + iTime / 2.;
	vec2 col = q + vec2(t, -t);

	vec2 tmp = col;
	col.x = sin(a * tmp.y) + cos(b * tmp.x * angle);
	col.y = sin(c * tmp.x) + cos(d * tmp.y * angle);

	fragColor = vec4(col, 0.5, 1.0);
}
